One.onLoad(class{static initValidation(){One.helpers("jq-validation"),jQuery(".js-validation").validate({ignore:[],
  rules:{
    "name":{required:!0,minlength:3},
    "email":{required:!0,email:!0},
    "password":{required:!0,minlength:5},
    "confirm_password":{required:!0,equalTo:"#password"},
    "suggestions":{required:!0,minlength:5},
    "skill":{required:!0},
    "currency":{required:!0,currency:["$",!0]},
    "website":{required:!0,url:!0},
    "phoneus":{required:!0,phoneUS:!0},
    "digits":{required:!0,digits:!0},
    "number":{required:!0,number:!0},
    "range":{required:!0,range:[1,5]},
    "terms":{required:!0},
    "city":{required:!0},
    "select2-multiple":{required:!0,minlength:2}},
  
  messages:{
    "name":{
      required:"Please enter a name",
      minlength:"Your name must consist of at least 3 characters"
    },
    "email":"Please enter a valid email address",
    "password":{
      required:"Please provide a password",minlength:"Your password must be at least 5 characters long"
    },
    "confirm_password":{required:"Please provide a password",minlength:"Your password must be at least 5 characters long",equalTo:"Please enter the same password as above"},"select2":"Please select a value!","select2-multiple":"Please select at least 2 values!","suggestions":"What can we do to become better?","skill":"Please select a skill!","currency":"Please enter a price!","website":"Please enter your website!","phoneus":"Please enter a US phone!","digits":"Please enter only digits!","number":"Please enter a number!","range":"Please enter a number between 1 and 5!","terms":"You must agree to the service terms!"}
  }),
    jQuery(".js-select2").on("change",(e=>{jQuery(e.currentTarget).valid()}))
  }static init(){this.initValidation()}}.init());