One.onLoad(class{static initValidation(){
  One.helpers("jq-validation"), jQuery(".js-validation-auth").validate({
    ignore: [],
    rules:{
      "fullname":{required:!0,minlength:3},
      "email":{required:!0,email:!0},
      'phone': { required: !0, minlength:10, maxlength:17 },
      "role": { required: !0 },
      "type": { required: !0 },
      "password":{required:!0,minlength:5},
      "password_confirmation":{required:!0,equalTo:"#password"},
      "terms":{required:!0}
    },


    messages:{
      "fullname":{
        required:"Please enter a fullname",
        minlength:"Your fullname must consist of at least 3 characters"
      },

      "phone": {
        required: "Please enter a phone",
        minlength: "Your phonenumber must consist of at least 10 characters",
        maxlength: "Your phonenumber must not exced 17 characters"
      },
        
      "email":"Please enter a valid email address",
      "password":{
        required:"Please provide a password",
        minlength:"Your password must be at least 8 characters long"
      },
      "password_confirmation":{
        required:"Please provide a password",
        minlength:"Your password must be at least 5 characters long",
        equalTo:"Please enter the same password as above"
      },
      "role": "Please select a value!",
      "type": "Please select a value!",
      "terms":"You must agree to the service terms!"
    }
  }), 
  
  jQuery(".js-select2").on("change", (e => { jQuery(e.currentTarget).valid() 
  }))}static init(){this.initValidation()}}.init());
